export const NAME="BreakpointHandler";export const BreakpointHandler=function(){const breakpoints=[{name:"xs",minPx:0},{name:"sm",minPx:576},{name:"md",minPx:768},{name:"lg",minPx:992},{name:"xl",minPx:1200},{name:"xxl",minPx:1400}];function dispatchBreakpointChangeEvent(detail){const event=new CustomEvent("breakPointChange",{detail:detail?.target||detail});document.dispatchEvent(event)}function setBreakpoints(){for(let x=0;x<breakpoints.length;x++){const {name,minPx}=breakpoints[x];const maxPx=breakpoints[x+
1]?.minPx-.02||0;const mediaQuery=`(min-width: ${minPx}px${maxPx?`) and (max-width: ${maxPx}px`:""})`;const MediaQueryList=window.matchMedia(mediaQuery);MediaQueryList.name=name;MediaQueryList.addEventListener("change",dispatchBreakpointChangeEvent);dispatchBreakpointChangeEvent(MediaQueryList)}}if(document.readyState!=="loading")setBreakpoints();else window.addEventListener("DOMContentLoaded",setBreakpoints);return setBreakpoints}();
