import{hnlLogger}from"./hnl.logger.mjs";const NAME="urlTools";export function changeUrlVar(key,value,navigate=true){if("URLSearchParams"in window){let searchParams=new URLSearchParams(window.location.search);let setValue=typeof value==="undefined"?"true":typeof value==="boolean"&&!value?"false":value;if(typeof value==="undefined"&&searchParams.get(key))searchParams.delete(key);else searchParams.set(key,setValue);if(navigate)window.location.search=searchParams.toString();else history.pushState("",
document.title,window.location.pathname+"?"+searchParams.toString())}else hnlLogger.warn(NAME,"Window has no search param support.")}export function init(elements){elements.forEach(element=>{element.changeUrlVar=changeUrlVar})};
